mapper.select:


 and expert_id = #{expertId}
 and expert_account = #{expertAccount}
 and expert_name like concat('%', #{expertName}, '%')
 and expert_position like concat('%', #{expertPosition}, '%')
 and expert_affiliation like concat('%', #{expertAffiliation}, '%')
 and primary_discipline = #{primaryDiscipline}
 and secondary_discipline = #{secondaryDiscipline}
 and tertiary_discipline = #{tertiaryDiscipline}
 and research_direction like concat('%', #{researchDirection}, '%')
 and thesis_id = #{thesisId}
 and project_id = #{projectId}
 and intellectual_property_id = #{intellectualPropertyId}
 and awards_id = #{awardsId}
 and requirement_id = #{requirementId}
 and expert_teams = #{expertTeams}
 AND FIND_IN_SET(#{categoryId}, SUBSTRING_INDEX(category_id, '、', -1))

ORDER BY CASE
    WHEN expert_position = '教授' THEN 0
    WHEN expert_position = '副教授' THEN 1
    WHEN expert_position = '主任医师' THEN 2
    WHEN expert_position = '主治医师' THEN 3
    WHEN expert_position = '医师' THEN 4
    WHEN expert_position = '讲师（高校）' THEN 5
    ELSE 6 
END

mapper.update:
update kyfz_expert
expert_account = #{expertAccount}, expert_name = #{expertName}, expert_position = #{expertPosition}, expert_affiliation = #{expertAffiliation}, primary_discipline = #{primaryDiscipline}, secondary_discipline = #{secondaryDiscipline}, tertiary_discipline = #{tertiaryDiscipline}, research_direction = #{researchDirection}, thesis_id = #{thesisId}, project_id = #{projectId}, intellectual_property_id = #{intellectualPropertyId}, awards_id = #{awardsId}, requirement_id = #{requirementId}, expert_teams = #{expertTeams}, expert_significance = #{expertSignificance},
where expert_id = #{expertId}

UPDATE kyfz_match
SET mark_project = CONCAT_WS('、', 
CASE 
    WHEN mark_project IS NULL OR TRIM(mark_project) = '' THEN NULL 
    ELSE TRIM(BOTH ' ' FROM mark_project) 
END, 
#{markProject}
) 
WHERE match_id=#{matchId}

UPDATE kyfz_match
SET mark_work = CONCAT_WS('、', 
CASE 
    WHEN mark_work IS NULL OR TRIM(mark_work) = '' THEN NULL 
    ELSE TRIM(BOTH ' ' FROM mark_work) 
END, 
#{markWork}
) 

WHERE match_id=#{matchId}

UPDATE kyfz_match
SET mark_thesis = CONCAT_WS('、', 
CASE 
    WHEN mark_thesis IS NULL OR TRIM(mark_thesis) = '' THEN NULL 
    ELSE TRIM(BOTH ' ' FROM mark_thesis) 
END, 
#{markThesis}
) 
WHERE match_id=#{matchId}

UPDATE kyfz_match
SET mark_certificate = CONCAT_WS('、', 
    CASE 
        WHEN mark_certificate IS NULL OR TRIM(mark_certificate) = '' THEN NULL 
        ELSE TRIM(BOTH ' ' FROM mark_certificate) 
    END, 
    #{markCertificate}
) 
WHERE match_id = #{matchId}
