mapper.select:
select enterprise_id, enterprise_name,
       enterprise_credit_code, enterprise_describe, registered_capital, create_by, create_time,
       update_by, update_time from kyfz_enterprise

            and enterprise_name like
       concat('%', #{enterpriseName}, '%')
            and
       enterprise_credit_code = #{enterpriseCreditCode}
            and
       enterprise_describe = #{enterpriseDescribe}
            and
       registered_capital = #{registeredCapital}
            and
       enterprise_keywords = #{enterpriseKeywords}

mapper.update:
update kyfz_requirement

           requirement_status = '已匹配',

       where requirement_id = #{requirementId}

mapper.select:
select p.requirement_id, p.project_name,
       p.contract_number, p.client, p.project_leader, p.project_leader_job_number,
       p.project_members, p.support_unit, p.support_unit_number, p.project_category,
       p.project_type, p.project_classification, p.project_nature, p.service_national_economy,
       p.national_economy_sub_industry, p.socio_economic_objective, p.start_project_time,
       p.end_project_time, p.total_budget, p.contract_type, p.first_discipline,
       p.secondary_discipline, p.project_leader_unit, p.enterprise_number, p.campus_member_account,
       p.requirement_status, p.creat_by, p.create_time, p.update_by, p.update_time,
       p.requirement_keywords, p.requirement_description, p.requirement_release_time,
       kyfz_enterprise.enterprise_name, kyfz_enterprise.enterprise_describe,
       kyfz_enterprise.registered_capital, kyfz_enterprise.enterprise_Keywords from
       kyfz_staging_requirement p LEFT JOIN kyfz_enterprise ON p.enterprise_number =
       kyfz_enterprise.enterprise_id left join sys_user u on p.user_id=u.user_id left join sys_dept
       d on d.dept_id=u.dept_id

            and project_name like concat('%',
       #{projectName}, '%')
            and
       contract_number = #{contractNumber}
            and
       client = #{client}
            and
       project_leader = #{projectLeader}
            and
       project_leader_job_number = #{projectLeaderJobNumber}
            and project_members =
       #{projectMembers}
            and
       support_unit = #{supportUnit}
            and
       support_unit_number = #{supportUnitNumber}
            and project_category like
       concat('%', #{projectCategory}, '%')
            and project_type like concat('%',
       #{projectType}, '%')
            and
       project_classification = #{projectClassification}
            and project_nature =
       #{projectNature}
            and
       service_national_economy = #{serviceNationalEconomy}
            and
       national_economy_sub_industry = #{nationalEconomySubIndustry}
            and
       socio_economic_objective = #{socioEconomicObjective}
            and start_project_time = #{startProjectTime}
            and end_project_time = #{endProjectTime}
            and total_budget = #{totalBudget}
            and contract_type =
       #{contractType}
            and
       first_discipline = #{firstDiscipline}
            and
       secondary_discipline = #{secondaryDiscipline}
            and
       project_leader_unit = #{projectLeaderUnit}
            and enterprise_name like
       concat('%', #{enterpriseName}, '%')
            and
       campus_member_account = #{campusMemberAccount}
            and
       requirement_status like concat('%', #{requirementStatus}, '%')
            and creat_by = #{creatBy}
            and
       requirement_keywords = #{requirementKeywords}
            and
       requirement_description = #{requirementDescription}
            and DATE_FORMAT(requirement_release_time,
       '%Y-%m') = DATE_FORMAT(#{requirementReleaseTime}, '%Y-%m')
            and task_id = #{taskId}
       ${params.dataScope}
        ORDER BY requirement_id DESC

mapper.delete:
delete from
       kyfz_staging_requirement where requirement_id in
        #{requirementId}
